			},
			onLocaleChange(e) {
			if (this.isAndroid) {
				uni.showModal({
				content: this.$t('index.language-change-confirm'),
				success: (res) => {
					if (res.confirm) {
					uni.setLocale(e.code);
					}
				}
				})
			} else {
