					{ title: '国际小包' }
				]
      }
    },
    computed:{
      locales() {
        return [{
            text: this.$t('locale.auto'),
            code: 'auto'
          }, {
            text: this.$t('locale.en'),
            code: 'en'
          },
          {
            text: this.$t('locale.zh-hans'),
            code: 'zh-Hans'
          },
          {
            text: this.$t('locale.zh-hant'),
            code: 'zh-Hant'
          },
          {
            text: this.$t('locale.ja'),
            code: 'ja'
          }
        ]
      },
			specialPriceGroup() {
				const groups = [];
				for (let i = 0; i < this.specialPrice.length; i += 4) {
					groups.push(this.specialPrice.slice(i, i + 4));
				}
        return groups;
			}
    },
    onLoad() {
      let systemInfo = uni.getSystemInfoSync();

      this.systemLocale = systemInfo.language;
      this.applicationLocale = uni.getLocale();
			console.log(22222)
			console.log(this.applicationLocale)
      this.isAndroid = systemInfo.platform.toLowerCase() === 'android';
      uni.onLocaleChange((e) => {
        this.applicationLocale = e.locale;
      })
    },
    methods: {
			onSwiperChange(event) {
        this.currentIndex = event.detail.current;
			},
			toLeft() {
				if (this.currentIndex > 0) {
					this.currentIndex--;
				}
			},
			toRight() {
				if (this.currentIndex < this.specialPriceGroup.length - 1) {
					this.currentIndex++;
				}
			},
			switchToChinese() {
        uni.setLocale('zh-Hans');
				this.$i18n.locale = 'zh-Hans';
        uni.navigateTo({ 
					url: '/pages/index/index'
				});
      },
			changeLang() {
				uni.setLocale('en');
				this.$i18n.locale = 'en';
				uni.navigateTo({ 
					url: '/pages/index_en/index'
				});
			},
			handleClick() {
				console.log(this.value)
				const trackId = this.value;
				// if(index === 1) {
				uni.navigateTo({ 
					url: '/pages/index/search_track?track_id=' + trackId
				});
				// }
			},
			onLocaleChange(e) {
			if (this.isAndroid) {
				uni.showModal({
				content: this.$t('index.language-change-confirm'),
				success: (res) => {
					if (res.confirm) {
					uni.setLocale(e.code);
					}
				}
				})
			} else {
				uni.setLocale(e.code);
				this.$i18n.locale = e.code;
			}
			}
    }
  }
</script>

